skillInfo = GuideConfig_getSkillUpgradeInfo()	local data = LayerSkillDevelop.getData()	if skillInfo.id ~= data.id then		return	end	if "ui_skilldevelop" == condition then		GuideUnforce_ClickSkillUpGrade["config"] = 		{			{cmd = "click_btn",	param = "parent_name=SkillDevelopPanel,node_name=Button_upgrade", lock = 2, title = "GUIDE_TITLE_12"},		}	end	GuideUIEngine.guideStart(GuideUnforce_ClickSkillUpGrade, false)end------------------------------------------------------------------------------------------------ 第17组:指引装备强化------------------------------------------------------------------------ 点击主界面锻造GuideUnforce_ClickForgeBuild = {	["ui_condition"]	= {"ui_main"},	["copy_id"]			= 1005,	["config"]			= 	{		{cmd = "wait_msg",		param = "",													msg = "click_copy_delock"},		{cmd = "click_btn",		param = "parent_name=Panel_main_enter,node_name=Panel_forge_build",	title = "GUIDE_TITLE_01",	direct = "up"},	},}function GuideUnforce_ClickForgeBuild.guideStart()	GuideUIEngine.guideStart(GuideUnforce_ClickForgeBuild, false)end-- 点击锻造强化按钮GuideUnforce_ClickForgeStrengthen= {	["ui_condition"]	= {"ui_smithstrength"},	["copy_id"]			= 1005,}function GuideUnforce_ClickForgeStrengthen.guideStart()	local function func1()		local rootName, widgetName = LayerSmithStrength.getWidgetNameByStrength()		return rootName	end	local function func2()		local rootName, widgetName = LayerSmithStrength.getWidgetNameByStrength()		return widgetName	end	GuideUnforce_ClickForgeStrengthen["config"] = 	{		{cmd = "click_btn",		param = "parent_name=f1,node_name=f2", funcs = {f1=func1,f2=func2},	title = "GUIDE_TITLE_01", direct = "up"},	}	GuideUIEngine.guideStart(GuideUnforce_ClickForgeStrengthen, false)end------------------------------------------------------------------------------------------------ 第18组:指引选择援军------------------------------------------------------------------------ 选择援军GuideUnforce_SelectAssistance = {	["ui_condition"]	= {"ui_assistan