------------------------------------------------------------------------ 作者：lewis-- 日期：2013-4-4-- 描述：更新属性视图----------------------------------------------------------------------StepPlayerBuff = class(BattleStep)--构造function StepPlayerBuff:ctor()	self.mParam		= "buff"	self.mName		= "update_info"end--初始化function StepPlayerBuff:init()end--执行对应的操作function StepPlayerBuff:excute()	local playerTB 		= RoleMgr.getConfig("rmc_player_table")	for key, player in pairs(playerTB) do		if player:isAlive() then			--player:onStep(self.mParam)			player:updateInfoView(self.mParam)		end	endend