------------------------------------------------------------------------ 作者：lewis-- 日期：2013-4-4-- 描述：每个角色的回合,刷新数据----------------------------------------------------------------------StepRoleRound = class(BattleStep)--构造function StepRoleRound:ctor()	self.mRole		= nil	self.mName		= "role_round"end--初始化function StepRoleRound:init(role)	self.mRole = roleend--执行对应的操作function StepRoleRound:excute()	self.mRole:changePortraits()	self.mRole:changeShader()	self.mRole:roundStep()end