------------------------------------------------------------------------ 作者：lewis-- 日期：2013-4-4-- 描述：技能特效----------------------------------------------------------------------StepSkillEffect = class(BattleStep)--构造function StepSkillEffect:ctor()	self.mEffect = nil	self.mName		= "skill_effect"end--初始化function StepSkillEffect:init(casterPos, targetPosVec, id)	self.mEffect = EffectSkill.new()	self.mEffect:init(casterPos, targetPosVec, id)end--执行对应的操作function StepSkillEffect:excute()	self.mEffect:play()end--获取作用时间表function StepSkillEffect:getDuration()	return self.mEffect:duration()end