------------------------------------------------------------------------ 作者：lewis-- 日期：2013-4-4-- 描述：更新属性视图----------------------------------------------------------------------StepUpdateSkill = class(BattleStep)--构造function StepUpdateSkill:ctor()	self.mParam		= "skill"	self.mName		= "update_skill"end--初始化function StepUpdateSkill:init()end--执行对应的操作function StepUpdateSkill:excute()	local playerTB 		= RoleMgr.getConfig("rmc_player_table")	local monsterTB 	= RoleMgr.getConfig("rmc_monster_table")   if FightDateCache.getData("fd_copy_id") == FIRSTCOPYID then
       local skillFont = Effectskillfont.new();       skillFont:play()
   end	for key, monster in pairs(monsterTB) do		if monster:isAlive() then			monster:updateInfoView(self.mParam)		end	end		for key, player in pairs(playerTB) do		if player:isAlive() then			player:updateInfoView(self.mParam)		end	endend