------------------------------------------------------------------------ 作者：lewis-- 日期：2013-3-31-- 描述：角色状态数据----------------------------------------------------------------------DataStatus = class()--构造function DataStatus:ctor()	local tb = {}	--特殊技能状态	tb["status_stealth"]				= false					--是否潜行	tb["status_amplify_damage"]			= 0.0					--伤害加深倍率,对所有伤害	tb["status_damage_rebound_rate"]	= 0.0					--物理伤害反弹倍率,对普通伤害    tb["status_magic_damage_rate"]      = 0                     --魔法伤害反弹倍率,对魔法伤害          tb["status_damage_rebound_value"]	= 0					    --物理伤害反弹固定数值,对普通伤害    tb["status_magic_damage_value"]      = 0                    --魔法伤害反弹固定数值,对魔法伤害        	tb["human_shield_damage_reduce"]	= 0.0					--肉盾伤害减免率,对所有伤害	tb["status_damege_health_rate"]		= 0						--伤害吸血率,对普通伤害	tb["status_magic_immune_rate"]		= 0						--魔法免役率     tb["status_damage_reduce_value"]		= 0				    --伤害减少固定数值,对所有伤害	--buff状态    tb["rebound"]			= 0			--反伤	tb["life"]				= 0			--生命	tb["multicast"]			= 0 		--多重施法	tb["disarm"]			= false		--缴械	tb["uncontrollable"]	= false		--不可操作	tb["damage_reduction"]	= 0			--伤害减免	tb["mask_of_eath"]		= 0			--吸血	tb["cleaned"]			= false		--净化	tb["hex"]				= 0			--妖术	tb["max_cd"]			= false		--重新冷却    tb["mac_cd_single"]     = false     --	tb["silenced"]			= false		--沉默    tb["absorption"]        = 0         --剩余吸收伤害数值    --特殊强化BUFF     tb["dcsy"]              = false    --洞察圣印    tb["ryzs"]              = false    --熔岩灼烧    tb["bsxr"]              = false    --冰霜虚弱    tb["hbz"]               = false    --寒冰指    tb["zs"]                = false    --灼烧    tb["asns"]              = false    --奥术凝视	self.status = tbend--设置状态function DataStatus:setStatus(name, value)	local status = self.status	if status[name] == nil then		return	end	status[name] = valueend--获取状态function DataStatus:getStatus(name)	local status = self.status	if status[name] == nil then		return nil	end	return status[name]end